import 'dotenv/config';
import { MongoMemoryServer } from 'mongodb-memory-server';
import { initializeApp } from '../server/server';

export async function mochaGlobalSetup(this: {
  close: () => Promise<void>;
  mongod: MongoMemoryServer;
}) {
  try {
    this.mongod = await MongoMemoryServer.create({
      instance: {
        port: parseInt(process.env.MONGO_PORT ?? '27017', 10),
        ip: process.env.MONGO_HOSTNAME,
        dbName: process.env.MONGO_DATABASE,
      },
      binary: {},
    });
    const [app, close] = await initializeApp();
    const server = await app.listen(8888);
    this.close = async () => {
      await close();
      await server.close();
    };
  } catch (err) {
    console.error(err);
  }
}

import bodyParser from 'body-parser';
import cookieParser from 'cookie-parser';
import Express from 'express';
import { buildProviderModule } from 'inversify-binding-decorators';
import { isNil } from 'lodash';
import { Connection } from 'mongoose';
import morgan from 'morgan';
import 'reflect-metadata';
import config from '../common/configuration';
import { iocContainer } from '../common/ioc';
import Logger from '../common/logger';
import { MongoRepository } from '../db/mongo/mongo-db';
import { MongoPersistence } from '../db/Repository';
import error400Middleware from '../middleware/400.mw';
import error404Middleware from '../middleware/404.mw';
import error500Middleware from '../middleware/500.mw';
import { RegisterRoutes } from '../routes';

const logger = Logger.child({ service: 'App' });

export async function initializeApp() {
  try {
    const mongo: MongoPersistence = new MongoRepository();

    logger.info('Creating Mongoose connection');
    const mongooseConnection = await mongo.getConnection();
    logger.info('Mongoose connection successfully created');

    if (isNil(mongooseConnection))
      throw new Error('Unable to connect to mongo');

    logger.info('Binding Mongoose connection to IoC Container');
    iocContainer
      .bind<Connection>(Connection)
      .toConstantValue(mongooseConnection);

    iocContainer.load(buildProviderModule());

    const app = Express();

    app.use(bodyParser.urlencoded({ extended: true }));
    app.use(bodyParser.json());
    app.use(cookieParser(config.cookieSecret));
    app.use(
      morgan('tiny', { stream: { write: (message) => logger.info(message) } })
    );

    RegisterRoutes(app);

    app.use(error400Middleware);
    app.use(error500Middleware);

    app.use(error404Middleware);

    async function close() {
      logger.info('Closing Mongo connection');
      await mongo.close();

      setTimeout(function () {
        console.error('Could not close connections in time, forcing shut down');
        process.exit(1);
      }, 30 * 1000);

      process.exit();
    }

    process.on('SIGINT', async () => {
      logger.info('Handling SIGINT Signal');
      await close();
    });
    process.on('SIGTERM', async () => {
      logger.info('Handling SIGTERM Signal');
      await close();
    });
    logger.info('App successfully initialized');

    return [app, close] as const;
  } catch (error) {
    logger.error('Unhandled error initializing application', { error });
    throw error;
  }
}

import 'dotenv/config';

export interface Configuration {
  cookieSecret: string;
  port: number;
  mongo: {
    hostname: string;
    port: number;
    username: string;
    password: string;
    database: string;
  };
  loggerLevel: string;
}

const config = {
  cookieSecret: process.env.COOKIE_SECRET,
  port: parseInt(process.env.PORT ?? '', 10),
  mongo: {
    hostname: process.env.MONGO_HOSTNAME,
    port: parseInt(process.env.MONGO_PORT || '', 10),
    username: process.env.MONGO_USERNAME,
    password: process.env.MONGO_PASSWORD,
    database: process.env.MONGO_DATABASE,
    useMemoryServer: process.env.MONGO_USE_MEMORY_DATABASE ?? false,
  },
  loggerLevel: process.env.LOGGER_LEVEL,
};

Object.entries(config).forEach(([key, value]) => {
  if (value == null || value == NaN) {
    throw new Error(`Configuration was missing for ${key}`);
  }
});

export default config;

import { Container, decorate, inject, injectable, interfaces } from 'inversify';
import {
  autoProvide,
  fluentProvide,
  provide,
} from 'inversify-binding-decorators';
import { Controller } from 'tsoa';

// Assign a container to `iocContainer`.
const iocContainer = new Container();

decorate(injectable(), Controller);

const provideSingleton = function <T>(
  identifier: interfaces.ServiceIdentifier<T>
) {
  return fluentProvide(identifier).inSingletonScope().done(true);
};

// export according to convention
export {
  iocContainer,
  provideSingleton,
  injectable,
  inject,
  provide,
  autoProvide,
  decorate,
};

import winston, { format, transports } from 'winston';
import config from './configuration';

const Logger = winston.createLogger({
  level: config.loggerLevel,
  format: format.combine(
    format.prettyPrint(),
    format.timestamp(),
    format.colorize()
  ),
  transports: [new transports.Console()],
});

export default Logger;

import { Connection, createConnection } from 'mongoose';
import config from '../../common/configuration';
import Logger from '../../common/logger';
import { MongoPersistence } from '../Repository';

export class MongoRepository extends MongoPersistence {
  private connection: Connection | undefined;

  private logger = Logger.child({ name: 'MongoDB' });

  async close() {
    if (this.connection) {
      await this.connection.close(true);
    }
  }

  constructor() {
    super();
  }

  async getConnection() {
    try {
      const connectionString = `mongodb://${config.mongo.hostname}:${config.mongo.port}`;
      this.logger.info(`Connecting to MongoDb @ ${connectionString} `);

      return await createConnection(connectionString, {
        dbName: config.mongo.database,
        ...(config.mongo.username && config.mongo.password
          ? {
              authSource: 'admin',
              auth: {
                username: config.mongo.username,
                password: config.mongo.password,
              },
            }
          : {}),
      }).asPromise();
    } catch (err) {
      this.logger.error(`Error connecting to mongo`, err);
      throw new Error('Failed to establish connection to Mongo');
    }
  }
}

import { Connection } from 'mongoose';

export interface Repository<EntityType> {
  create(model: EntityType): Promise<EntityType>;
  update(query: any, model: EntityType): Promise<[number, ...EntityType[]]>;
  delete(query: any): Promise<number>;
  find(query: any, options: FindOptions): Promise<EntityType[]>;
  findOne(query: any, options: FindOptions): Promise<EntityType | null>;
}

export interface FindOptions {
  skip?: number;
  limit?: number;
  sort?: SortOptions;
}

export type SortOptions =
  | 'asc'
  | 'desc'
  | 'ascending'
  | 'descending'
  | '1'
  | '-1';

export abstract class MongoPersistence {
  abstract getConnection(): Promise<Connection>;
  abstract close(): Promise<void>;
}

import { NextFunction, Request, Response } from 'express';
import { ValidateError } from 'tsoa';
import Logger from '../common/logger';

const logger = Logger.child({ service: 'ValidateErrorMiddleware' });

export default function (
  err: Error,
  req: Request,
  res: Response,
  next: NextFunction
) {
  if (err instanceof ValidateError) {
    logger.warn(err.message, {
      fields: err.fields,
    });
    return res.status(err.status).json({
      message: 'Validation Failed',
      details: err?.fields,
    });
  }
  next(err);
}

import { Request, Response } from 'express';
import Logger from '../common/logger';

const logger = Logger.child({ service: '404Middleware' });
export default function (req: Request, res: Response) {
  logger.info('Route not found', { url: req.originalUrl });
  return res.status(404).send(`${req.originalUrl} not found`);
}

import { NextFunction, Request, Response } from 'express';
import Logger from '../common/logger';

const logger = Logger.child({ service: '500ErrorMiddleware' });

export default function (
  err: Error,
  _req: Request,
  res: Response,
  _next: NextFunction
) {
  if (err instanceof Error) {
    logger.error(err);
    res.status(500).json({
      message: 'Internal Server Error',
    });
  }
}

